import { useState } from 'react'

const Books = (props) => {
  const [genre, setGenre] = useState('all genres')

  if (!props.show) {
    return null
  }

  const books = [...props.books]
  const genres = [...new Set(books.flatMap((book) => book.genres))]
  const genreList = genres.concat('all genres')
  const filteredBooks =
    genre === 'all genres'
      ? books
      : books.filter((book) => book.genres.includes(genre))

  return (
    <div>
      <h2>books</h2>

      <table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {filteredBooks.map((a) => (
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {genreList.map((genre) => (
        <button key={genre} onClick={() => setGenre(genre)}>
          {genre}
        </button>
      ))}
    </div>
  )
}

export default Books
